import React, {useState, useEffect, useRef} from 'react';
import Header from './header'
import Menu from './menu'
import Profile from './profile'
import ModalName from './modalName'
import Search from './search'

import '../css/index.css'
import './menu'

function App() {

    const [name, setName] = useState("unnamed");
    const [currentTab, setCurrentTab] = useState("profile");
    const [showModalName, setShowModalName] = useState("");

    const changeName = (newName) => {
        setName(newName);
    }

    const openModalName = () => {
        setShowModalName(true);
    }
    const closeModalName = () => {
        setShowModalName(false);
    }
    const changeCurrentTab = (tab) => {
        setCurrentTab(tab);
    }


    return (
        <div className="main">
        <ModalName show={showModalName} 
        onClose={closeModalName} 
        currentName={name} 
        changeName={changeName}
        />
        <Header name={name}/>
        <div className="middleBlock">
        <Menu changeCurrentTab = {changeCurrentTab}/>
        <Search currentTab={currentTab}/>
        <Profile name={name} openModalName={openModalName} currentTab={currentTab}/>
        </div>
      </div>
    )
}

export default App;
