    if (currentTab === "test") {
        return (
            <div>
            <ul>
                {users.map((user, index) => (
                <li key={index}>{user.name} - {user.age} лет</li>
                ))}
            </ul>
            <ul>
                {numbers.map((number) => <li key={number.toString()}>{number}</li>)}
            </ul>

            <div>Имя: {user.name} <br/>Почта: {user.email} <br/> Телефон: {user.phone}</div>
            </div>
        )
    }
}
export default Test
