import React, {useState, useEffect, useRef} from 'react';
import Header from './header'
import Menu from './menu'
import Profile from './profile'
import ModalName from './modalName'

import '../css/index.css'
import './menu'

function App() {

    const [name, setName] = useState("");
    const [showModalName, setShowModalName] = useState("");

    const changeName = (newName) => {
        setName(newName);
    }

    const openModalName = () => {
        setShowModalName(true);
        alert(showModalName)
    }
    const closeModalName = () => {
        setShowModalName(false);
    }


    return (
        <div className="main">
            <button onClick={openModalName}>Yffff</button>
        <ModalName show={showModalName}/>
        <Header />
        <div className="middleBlock">
        <Menu />
        <Profile name={name} changeName={changeName}/>
        </div>
      </div>
    )
}

export default App;
