import React, {useState} from 'react';
import '../css/modalName.css'

function ModalName({show, children, onClose}) {
    if (show == false) {
        return null;
    }
    else {
        return(
            <div className="modalName" onClick={onClose}>
                <div className="modalName__content">
                {children}
                </div>
            </div>
        )
    }
}


export default ModalName
