import React, {useState} from 'react';
import '../css/menu.css'

//Создаём меню
function Menu({changeCurrentTab, currentTab}) {
    const styleChosen = {
        backgroundColor:'grey'
    }

    const setCurrentTab = (tab) => {
        changeCurrentTab(tab);
    }

    return(
        <div className="menu">
            <div style = {currentTab === "profile" ? styleChosen : {styleChosen}}
             className="menu__profile menu__select"
            onClick={() => setCurrentTab("profile")
            }>Профиль</div>
            <div className="menu__search menu__select"
            onClick={() => setCurrentTab("search")}>Поиск</div>
            <div className="menu__objects menu__select">Объекты</div>
            <div className="menu__views menu__select">Просмотры</div>
            <div className="menu__documents menu__select">Документы</div>
            <div className="menu__test menu__select"
            onClick={() => setCurrentTab("test")}>Для теста</div>
        </div>
    )
}

export default Menu
