import React from "react";
import Header from './components/header'


function chat(message) {
    console.log(message);
  }
  
  
  class App extends React.Component {

    //Создание состояний
    constructor(props) {
        super(props)
        this.state = {
            name: "",
            currentInput: ""
        }
        this.changeName = this.changeName.bind(this);
        this.saveInput = this.saveInput.bind(this);  
    }

    //Рендер элементов
    render() {
      return ( <div className="card">
        <Header title="Это название и шапка"/>
        <h1>Ваша личная карта</h1>
        <input placeholder = {this.state.name} onClick={this.inputClick} onChange={this.saveInput()}/>
        <button onClick={this.changeName()}>Сохранить</button>
        <p>{this.state.name == "" ? "У вас нет имени!" : "У вас есть имя: " + this.state.name}</p>
      </div>)
    }
  
    inputClick() {
      console.log("1");
    }
    changeName() {
        this.setState({name: this.state.currentInput})
    }
    saveInput(event) {
        this.setState({currentInput: event.target.value})
    }
  }

  export default App
