import React from "react";
import Header from './components/header'


function chat(message) {
    console.log(message);
  }
  
  
  class App extends React.Component {

    //Создание состояний
    constructor(props) {
        super(props)
        this.state = {
            name: ""
        }
    }

    //Рендер элементов
    render() {
      return ( <div className="card">
        <Header title="Это название и шапка"/>
        <h1>Ваша личная карта</h1>
        <input placeholder = {this.name} onClick={this.inputClick} onChange={this.changeName}/>
        <p>{this.state.name == "" ? "У вас нет имени!" : "У вас есть имя!"}</p>
      </div>)
    }
  
    inputClick() {
      console.log("1");
    }
    changeName() {
        this.setState({name:"Новое имя"})
    }
  }

  export default App
