import React, { useState } from 'react';


function Button({text, clickCount}) {
    const [buttonText, setText] = useState(text);

    const handleClick = () => {
        setText("Нажали");
        setClickCount(clickCount + 1);
    }

    return (
        <button onClick={handleClick}>{buttonText}</button>
    );
}

export default Button;
